sd-cwt-types = sd-cwt-issued / kbt-cwt
sd-cwt-issued = #6.18([ protected: bstr .cbor sd-protected, sd-unprotected, payload: bstr .cbor sd-payload, signature: bstr ])
kbt-cwt = #6.18([ protected: bstr .cbor kbt-protected, kbt-unprotected, payload: bstr .cbor kbt-payload, signature: bstr ])
sd-protected = { &(typ: 16) ^ => "application/sd+cwt", &(alg: 1) ^ => int, &(sd_alg: TBD2) ^= int, ; -16 for sha-256 * key => any }
kbt-protected = { &(typ: 16) ^ => "application/kb+cwt", &(alg: 1) ^ => int, &(kcwt: 13) ^ => bstr .cbor sd-cwt-issued, * key => any }
sd-unprotected = { ? &(sd_claims: TBD1) ^ => bstr .cbor salted-array, * key => any }
kbt-unprotected = { * key => any }
sd-payload = { ; standard claims &(iss: 1) ^ => tstr, ; "https://issuer.example" ? &(sub: 2) ^ => tstr, ; "https://device.example" ? &(aud: 3) ^ => tstr, ; "https://verifier.example" ? &(exp: 4) ^ => int, ; 1883000000 ? &(nbf: 5) ^ => int, ; 1683000000 ? &(iat: 6) ^ => int, ; 1683000000 &(cnf: 8) ^ => { * key => any }, ; key confirmation ; ? &(redacted_claim_keys: TBD3) ^ => [ * bstr ], * key => any }
kbt-payload = { &(aud: 3) ^ => tstr, ; "https://verifier.example" ? &(exp: 4) ^ => int, ; 1883000000 ? &(nbf: 5) ^ => int, ; 1683000000 &(iat: 6) ^ => int, ; 1683000000 ? &(cnonce: 39) ^ => bstr, * key => any }
salted-array = [ +bstr .cbor salted ] salted = salted-claim / salted-element / decoy salted-claim = [ bstr .size 16, ; 128-bit salt (int / text), ; claim name any ; claim value ] salted-element = [ bstr .size 16, ; 128-bit salt any ; claim value ] decoy = [ bstr .size 16 ; 128-bit salt ]
key = int / text TBD1 = 17 TBD2 = 18 ;TBD3 = #6.58(any) ; CBOR tag wrapping to-be-redacted keys or elements TBD4 = #6.59(0) ; CBOR tag 59 wrapping reserved integer claim key 0 ;TBD5 = #6.60( bstr ) ;redacted_claim_element
